namespace Auth;

public interface ITokenService
{
  string Issue(string subject);
  bool TryValidate(string token, out string subject);
}

namespace Auth;

// Placeholder: implement signing/validation via your chosen library.
public sealed class TokenService : ITokenService
{
  public string Issue(string subject) => "token:" + subject;

  public bool TryValidate(string token, out string subject)
  {
    subject = "";
    if (!token.StartsWith("token:")) return false;
    subject = token.Substring("token:".Length);
    return true;
  }
}
