package main

import (
  "flag"
  "fmt"
  "log"
  "os"
)

func main() {
  name := flag.String("name", "world", "name to greet")
  verbose := flag.Bool("v", false, "verbose logging")
  flag.Parse()

  log.SetOutput(os.Stdout)
  if *verbose {
    log.Printf("verbose enabled")
  }

  fmt.Printf("Hello, %s\n", *name)
}
