package ${groupId};

import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Properties;
import java.io.FileInputStream;

public final class HttpSample {
  public static void run() throws Exception {
    Properties p = new Properties();
    try (FileInputStream in = new FileInputStream("config.properties")) {
      p.load(in);
    }
    String base = p.getProperty("baseUrl", "https://example.com");
    System.out.println("GET " + base + "/");
    HttpClient c = HttpClient.newHttpClient();
    HttpRequest r = HttpRequest.newBuilder().uri(new URI(base + "/")).GET().build();
    HttpResponse<String> resp = c.send(r, HttpResponse.BodyHandlers.ofString());
    System.out.println("Status: " + resp.statusCode());
  }
}
