async function withBackoff(op, attempts=3, baseDelayMs=150){
  let last;
  for(let i=0;i<attempts;i++){
    try { return await op(); }
    catch(e){ last=e; const d=baseDelayMs*Math.pow(2,i); await new Promise(r=>setTimeout(r,d)); }
  }
  throw last || new Error('Retry failed');
}
module.exports = { withBackoff };
