import time

def with_backoff(op, attempts=3, base_delay_ms=150):
    last = None
    for i in range(attempts):
        try:
            return op()
        except Exception as e:
            last = e
            delay = base_delay_ms * (2 ** i) / 1000.0
            time.sleep(delay)
    raise last if last else RuntimeError('Retry failed')
